DROP TABLE IF EXISTS [Employees];
CREATE TABLE [Employees] ( [EmployeeID] INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL , [LastName] nvarchar(20) NOT NULL COLLATE NOCASE , [FirstName] nvarchar(10) NOT NULL COLLATE NOCASE , [Title] nvarchar(30) NULL COLLATE NOCASE , [TitleOfCourtesy] nvarchar(25) NULL COLLATE NOCASE , [BirthDate] datetime NULL , [HireDate] datetime NULL , [Address] nvarchar(60) NULL COLLATE NOCASE , [City] nvarchar(15) NULL COLLATE NOCASE , [Region] nvarchar(15) NULL COLLATE NOCASE , [PostalCode] nvarchar(10) NULL COLLATE NOCASE , [Country] nvarchar(15) NULL COLLATE NOCASE , [HomePhone] nvarchar(24) NULL COLLATE NOCASE , [Extension] nvarchar(4) NULL COLLATE NOCASE , [Photo] image NULL , [Notes] ntext NULL , [ReportsTo] int NULL , [PhotoPath] nvarchar(255) NULL COLLATE NOCASE , CONSTRAINT [FK_Employees_0_0] );
DROP TABLE IF EXISTS [Customers];
CREATE TABLE [Customers] ( [CustomerIDCopy] nchar(5) NOT NULL COLLATE NOCASE , [CompanyNameCopy] nvarchar(40) NOT NULL COLLATE NOCASE , [ContactNameCopy] nvarchar(30) NULL COLLATE NOCASE , [ContactTitleCopy] nvarchar(30) NULL COLLATE NOCASE , [AddressCopy] nvarchar(60) NULL COLLATE NOCASE , [PostalCodeCopy] nvarchar(10) NULL COLLATE NOCASE , [CountryCopy] nvarchar(15) NULL COLLATE NOCASE , [PhoneCopy] nvarchar(24) NULL COLLATE NOCASE , [FaxCopy] nvarchar(24) NULL COLLATE NOCASE , CONSTRAINT [sqlite_autoindex_Customers_1] PRIMARY KEY ([CustomerIDCopy]) );
